import { useEffect } from "react";

export default function Toast({ message, type = "success", onClose, duration = 3000 }) {
  // Auto-close after duration
  useEffect(() => {
    const timer = setTimeout(() => {
      onClose();
    }, duration);
    return () => clearTimeout(timer);
  }, [onClose, duration]);

  return (
    <div
      role="alert"
      className={`fixed top-5 right-5 z-50 flex items-center px-4 py-3 rounded-lg shadow-lg text-white gap-3
        ${type === "success" ? "bg-green-500" : "bg-red-500"} 
        animate-slide-in`}
    >
      <p className="text-sm font-medium">{message}</p>
      <button
        onClick={onClose}
        className="ml-2 text-white font-bold focus:outline-none hover:opacity-75"
      >
        âœ–
      </button>
    </div>
  );
}
