import { useEffect, useState } from "react";

export default function UploadedDetails() {
  const [patients, setPatients] = useState([]);

  useEffect(() => {
    fetch("http://localhost:4000/patients")
      .then(res => res.json())
      .then(data => setPatients(data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Uploaded Patient Details</h1>

      <div className="grid gap-4">
        {patients.map(patient => (
          <div
            key={patient._id}
            className="border rounded-lg p-4 shadow bg-white"
          >
            <p><strong>Name:</strong> {patient.name}</p>
            <p><strong>Age:</strong> {patient.age}</p>
            <p><strong>Gender:</strong> {patient.gender}</p>
            <p><strong>Disease:</strong> {patient.disease}</p>
            <p><strong>District:</strong> {patient.district}</p>
            <p><strong>Number:</strong> {patient.number}</p>
            <p><strong>Address:</strong> {patient.address}</p>

            {/* Display uploaded file if it's an image */}
            {patient.filePath && (
              <img
                src={`http://localhost:4000/${patient.filePath}`}
                alt="Uploaded file"
                className="mt-2 w-48 h-auto border rounded"
              />
            )}
          </div>
        ))}
      </div>
      <br/><br/>
      <button className="p-3 bg-green-500 color-black rounded-xl font-bold">Submit All <i class="fa-solid fa-paper-plane"></i></button>
    </div>
  );
}
